import { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchProducts, setSearchTerm } from '../redux/productSlice';
import ProductItem from './ProductItem';
import { productType } from '../propTypes';
import { motion } from 'framer-motion';
import { pageVariants, pageTransition } from '../utils/routeAnimations';

const ProductList = () => {
  const dispatch = useDispatch();
  const { items: products, status, error, searchTerm } = useSelector(state => state.products);

  <motion.div
    initial="initial"
    animate="in"
    exit="out"
    variants={pageVariants}
    transition={pageTransition}
  >
    {/* Blank */}
  </motion.div>

  useEffect(() => {
    dispatch(fetchProducts());
  }, [dispatch]);

  if (status === 'loading') return <ProductSkeleton count={6} />;
  if (status === 'failed') return <div>Error loading products</div>;
  const handleSearchChange = (e) => {
    dispatch(setSearchTerm(e.target.value));
  };

  const filteredProducts = products.filter(product =>
    product.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (status === 'loading') return <div>Loading products...</div>;
  if (status === 'failed') return <div>Error: {error}</div>;

  return (
    <div className="product-list">
      {products.map(product => (
        <ProductItem key={product.id} product={product} />
      ))}
    </div>
  );
};

ProductList.propTypes = {
  // Redux-injected props (if needed)
  products: PropTypes.array,
  loading: PropTypes.bool,
  error: PropTypes.string,
  fetchProducts: PropTypes.func,
  searchTerm: PropTypes.string,
  setSearchTerm: PropTypes.func
};

ProductItem.propTypes = {
  product: productType.isRequired
};
